import React, { useState } from 'react';
import './SupIncidentlist.css';

const staffMembers = [
  'John Doe',
  'Jane Smith',
  'Alice Johnson',
  'Bob Williams',
  // Add more staff members...
];

const incidentsData = [
  {
    id: 1,
    title: 'Network Outage',
    status: 'In progress',
    dateCreated: '2023-08-20',
    priority: 'High',
    assignedStaff: 'John Doe',
    reporter: 'Alice Smith',
    description: 'Users are unable to access the internet.',
  },
  {
    id: 2,
    title: 'Server Crashed',
    status: 'In Progress',
    dateCreated: '2023-08-21',
    priority: 'Medium',
    assignedStaff: 'Jane Smith',
    reporter: 'Bob Johnson',
    description: 'Critical server failure affecting multiple services.',
  },
  // Add more incidents...
];

const IncidentTable = () => {
  const [selectedIncident, setSelectedIncident] = useState(null);

  const handleIncidentClick = (incident) => {
    setSelectedIncident(incident === selectedIncident ? null : incident);
  };

  return (
    <div>
      <h1>Reported Incidents</h1>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Status</th>
            <th>Date Created</th>
            <th>Priority</th>
            <th>Assigned Staff</th>
          </tr>
        </thead>
        <tbody>
          {incidentsData.map((incident) => (
            <React.Fragment key={incident.id}>
              <tr
                onClick={() => handleIncidentClick(incident)}
                className={selectedIncident === incident ? 'selected' : ''}
              >
                <td>{incident.title}</td>
                <td>{incident.status}</td>
                <td>{incident.dateCreated}</td>
                <td>{incident.priority}</td>
                <td>{incident.assignedStaff}</td>
              </tr>
              {selectedIncident === incident && (
                <tr className="incident-details">
                  <td colSpan="5">
                    <p><strong>Reporter:</strong> {incident.reporter}</p>
                    <p><strong>Description:</strong> {incident.description}</p>
                    <div className="assign-section">
                      <select>
                        <option value="">Select staff member</option>
                        {staffMembers.map((staff, index) => (
                          <option key={index} value={staff}>{staff}</option>
                        ))}
                      </select>
                      <button>Assign</button>
                    </div>
                  </td>
                </tr>
              )}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default IncidentTable;
